import { Recipe } from "../recipes/recipe.model";
import { Injectable } from "@angular/core";
import { Ingredient } from "../shared/ingredient.model";
import { IngredientsService } from "./ingredients.service";
import { Subject } from "rxjs";

@Injectable()
export class RecipeService {
    //recipeSelected = new EventEmitter<Recipe>();
    RecetaUpdate = new Subject<Recipe[]>();
    private recipes: Recipe[] = [
        new Recipe('Pay de Wuayaba', 'Una muy wena receta pal pay ', 'data:image/jpeg;base64,',
            [
                new Ingredient('wuayaba', 2),
                new Ingredient('mawuayaba', 3)
            ]),
        new Recipe('Pay de Frambuesa', 'Lo mas delicioso del mundo', 'data:image/jpeg;base64,',
            [
                new Ingredient('frambuesa', 3),
                new Ingredient('maframbuesa', 1)
            ]),
        new Recipe('Pay de Pistache', 'No puedes comer solo uno ahre', 'data:image/jpeg;base64,',
            [
                new Ingredient('pitache', 2),
            ])
    ];
    getRecipes() {
        return this.recipes.slice();
    }
    constructor(private ingredientsService: IngredientsService) {

    }
    getRecipe(index: number) {
        return this.recipes[index];
    }
    addIngredientsToShoppingList(ingredients: Ingredient[]) {
        this.ingredientsService.addIngredients(ingredients);
    }

    addRecipe(recipe: Recipe) {
        this.recipes.push(recipe);
        this.RecetaUpdate.next(this.recipes.slice());
        console.log(this.getRecipes())
    }
    updateRecipe(index: number, recipe: Recipe) {
        this.recipes[index] = recipe;
        this.RecetaUpdate.next(this.recipes.slice());
    }



}